import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

const testTweet = {
  message: "Something about cats",
  gravatar: xyz,
  author: {
    handle: "catperson",
    name: "IAMA catperson"
  },
  likes: 2,
  retweets: 0,
  timestamp: "2016-07-30 21:24:37"

}

function Tweet({ tweet }) {
  return (
      <div className="tweet">
      <Avatar/>
        <div className="content">
          <NameWithHandle/>
          <Message/>
          <div className="buttons">
            <ReplyButton />
            <RetweetButton/>
            <LikeButton/>
            <MoreOptionsButton/>
          </div>
        </div>
      </div>
  );
}

function Avatar() {
  return(
    <img src="https://www.gravatar.com/avatar/nothing" className="avatar" alt="avatar" />
  );
}

function Message() {
  return (
    <div className="Message">
      This is less than 140 characters.
    </div>
  );
}

function NameWithHandle() {
  return (
    <span className="name-with-handle">
      <span className="name">
        Your Name
      </span>
      <span className="handle">
        @yourhandle
      </span>
    </span>
  )
}

const Time = () => {
  return (<span className="time">
    3h ago
  </span>)
}

const ReplyButton = () => {
  return <i className="fa fa-reply repply-button" />
}

const RetweetButton = () => {
  return <i className= "fa fa-retweet retweet-button" />
}

const LikeButton = () => {
  return <i className="fa fa-heart like-button" />
}

const MoreOptionsButton = () => {
  return <i className="fa fa-ellipsis-h more-options-button" />
}

ReactDOM.render(<Tweet/>, document.querySelector('#root'));
